INSERT INTO `subject` (`id`, `name`, `image`, `content`) VALUES
	(8, '가상증강현실의 이해', '1624504441307.jpg', '- AR/VR의 개념 \r\n- AR/VR 산업현황'),
	(9, '가상증강현실 개발환경', '1624504477380.png', '- 상용화 엔진 이해 \r\n- 3D 그래픽스의 이해'),
	(10, '프로그래밍 기본 문법 및 이론', '1624504509237.jpg', '- 프로그래밍 언어 기초 \r\n- 프로그래밍 언어 심화 \r\n- 프로그래밍 이론(자료구조.알고리즘, 디자인패턴)'),
	(11, '게임 프로그래밍 엔진', '1624504537556.png', '- 상용화 게임 엔진 고급 활용\r\n- AR/VR 기반의 앱 구동'),
	(12, 'AR/VR 응용 콘텐츠 개발', '1624504572140.png', '- 스테레오 카메라, GP5. QR코드 등을 활용한 AR/VR 개발 \r\n- AR/VR 주변기기 활용 \r\n- AR/VR 기획 및 개발 방법론 \r\n- VR 콘텐츠 개발 \r\n- AR 콘텐츠 개발 - 포트폴리오 발표회	'),
	(13, '취업 준비 과정', '1624504615075.jpg', '- 이력서, 자기소개서, 포트폴리오 소개서, 보유 기술 문서 작성\r\n- 포트폴리오 보안 및 강화 \r\n- 현업 실무자 초빙 모의면접 진행 \r\n- 수료 후, 취업할 때까지 강제 소환하여 취업 역량 강화 \r\n- 우수 인재 추천서를 통한 취업 지원'),
	(14, '블록체인의 개념 및 훈련 로드맵 소개', '1624592803113.jpg', ' Constell Fulmen의 서봉진 COO님이 현장 전문가 입장에서 \r\n블록체인의 역사, 개념, 미래 전망에 대해 특강'),
	(16, '자료구조 및 알고리즘', '1624591683840.png', '앱 자체가 블록체인 자원을 소모해 구동\r\n앱 최적화에 따른 실행속도, 소모비용 절감 등, \r\n합의 알고리즘에 따른 블록체인 시스템의 개선\r\n프로그래밍에서 활용되는 다양한 자료구조를 학습한다. \r\n자료정렬, 탐색 및 자료구조를 활용한 알고리즘 학습'),
	(17, '스마트 웹 프론트앤드 개발', '1624591816830.jpg', 'Javascript를 바탕으로 최신 라이브러리 활용\r\n\r\nHTML5, CSS#, Javascript를 활용하여 웹 기반\r\n서비스의 프론트앤드 개발 기초 학습\r\n\r\nReact 라이르러리를 활용한 프론트앤드 개발\r\n\r\n협약기업 현장 전문가를 통한 프로젝트 결과물 피드백'),
	(18, '스마트 웹 백앤드 개발', '1624591888886.png', '데이터베이스 학습\r\n\r\nNode.js를 활용한 웹 백앤드 개발'),
	(19, '블록체인 원리 및 핵심 기술의 이해', '1624592853187.jpg', 'Bearfoot Games 박재덕 대표님과 블록체인 웹 \r\n개발사 관계자들이 참여하여 현장 전문가 입장\r\n에서 블록체인의 원리와 핵심기술에 대한 특강'),
	(20, 'Javascript 프로그래밍 언어', '1624592265430.png', '4차 산업 앱 개발 트랜드에 따라 javascript를 사용해 제작된\r\n다양한 라이브러리, API, 프레임워크 등을 활용해 빠른 구현 \r\n및 test주기를 확보, 프론트 앤드, 벡엔드 기반 웹 개발 및 \r\n블록체인 개발에 광법위 하게 사용되는 Javascript 언어 기초 뭄법과\r\n객체지향의 개념을 학습');
INSERT INTO `teacher` (`id`, `name`, `position`, `title`, `career`, `content`, `image`, `show`) VALUES
	(1, '박병준', '원장', '더 나은 내일 My work, Tomorrow를 위한\r\n섬김의 리터', '경일직업능력개발원의 MC유재석\r\n도전을 두려워 하지 않는 젊은 경영인\r\n딸바보 아빠', '박병준 원장님은 냉철한 머리와 따뜻한 가슴으로 함께 살아가고자 하는\r\n이 시대의 경영자이자 아버지입니다.', 'profile_parkbyungjun.jpg', 1),
	(2, '유성준', '교무부장', '보완하되 무너 뜨리지 않는다.\r\n지지하되 반대하지 않는다.', '강의 및 실무 경력 18년\r\n정보처리 훈련교사 3급 \r\n본원 교무부장', '유성준 선생님은 직업교육이 산업현장과 사회적 동반자라는 가치를 가지고\r\n직무지식을 전달해야 한다고 생각합니다.\r\n항상 앞서가도록 노력하시는 젊은 정신의 직업 교육 전문가입니다.', 'profile_yooseongjun.jpg', 1),
	(3, '양현석', '게임 학과장', '좋은 회사에 들어가는 능력보다 좋은 회사를 \r\n만들어 가는 능력을 가르칩니다.', '현업 실무 13년, 강의 5년의 베테랑\r\n킹 오브 파이터즈 온라인 개발\r\n해외 게임 업체 기획 컨설팅', '양현석 선생님은 취업률 90%, 최고의 게임 교육을 위한\r\n과정 개발과 실무 훈련에 특화된 게임 교육 전문가 입니다.', 'profile_yanghyunseok.jpg', 1),
	(4, '여종락', '행정실장', '더 나은 내일 My work, Tomorrow를 위한 섬김의 리터', '직업 교육 행정의 베테랑\r\n차분하고 빈 틈 없는 행정 처리\r\n학생의 편에서 함께 고민하는 학생 지원 업무의 전문가', '여종락 선생님은 밥 잘 먹고 잠 잘자고, 일 잘하는,\r\n여러분이 안정적으로 훈련을 수행하는 데 필요한 여러 일상 행정 업무의 달인입니다.', 'profile_yeojongrak.jpg', 1),
	(5, '서혁준', '행정담당', '궁금한 건 무엇이든 물어보세요', '발로 뛰는 지원팀!\r\n성실과 열정의 아이콘\r\n일레트로닉 베이스 전문가', '친절하고 편안한 상담이 주특기인 서혁준 선생님은 과정에 대한\r\n모든 안내를 책임집니다. ', 'profile_seohyeokjun.jpg', 1),
	(6, '이재환', '게임 프로그래밍반', '저도 비전공자였습니다. 시작의 두려움을\r\n함께 깰 수 있게 도와주겠습니다.', '현업 실무 22년. 강의 경력 7년의 베테랑\r\n"시작하세요! Cocos2d-x 3.0 프로그래밍" 저자\r\n"핵심강좌! 유니티5" 저자', '이재환 선생님은 프로그래밍 전공자가 아닌 학생이 쉽고 빠르게 배울 수 있도록,\r\n전공자는 더욱 깊이 있게 배울 수 있도록 맞춤형으로 지도하는 게임 프로그래밍 서적\r\n저자이자 교육 베테랑입니다.', 'profile_leejaehwan.jpg', 1),
	(7, '김기원', '게임 기획반', '최고의 기획자가 되고 싶으면, 저와 함께\r\n하십시요. 길을 열어드리겠습니다.', 'RPG, SNG, 퍼즐, 슈팅, 전략시뮬\r\n겜블, 리듬게임, 레이싱, 호러\r\n다 장르 기획 경험', '김기원 선생님은 10여년 간 게임 기획의 지옥과 천국을 몸소 체험한 최강의\r\n기획력을 보유한 베테랑 게임 기획자입니다.', 'profile_kimkiwon.jpg', 1),
	(8, '정직한', '게임 기획반', '게이머의 껍질을 벗고. 게임 개발자로', '그라비티, 엔도어즈 등 게임업계 실무 경력 8년\r\n경남대학교 게임 교육원 기획과 담임교수\r\n게임, 소설, 영화를 넘나드는 종합 컨텐츠 제작자', '정직한 선생님은 실무 경력과 강의 경력을 고루 겸비한, 취업기간 동안\r\n밝고 긍정적인 면학 분위기를 조성하고 학생들의 지친 멘탈을 케어해 줄\r\n열정적인 서포터형 선생님입니다.', 'profile_jungjikhan.jpg', 1),
	(9, '오준환', '게임프로그래밍반', '프로그래머는 엔지니어입니다.\r\n공부와 연습 모두 중요합니다.', '8년의 현업, 2년의 강의 경력\r\n다양한 장르, 플랫폼 게임 개발 경험\r\n스타트업 개발중', '오준환 선생님은 학생들이 자신이 만든 게임을 오픈 마켓에 직접\r\n출시할 수 있도록 하는 것을 강의의 목표로 삼고 있습니다.\r\n여러분들의 포트폴리오가 곧 출시작이 될 것입니다.', 'profile_ohjunhwan.jpg', 1),
	(10, '양주현', '게임 프로그래밍반', '프로그래밍을 지배하는 자가 게임의\r\n생명력을 지배한다.', '현업실무 11년, 인디게임제작 3년차의 게임제작 전문가\r\n\'신디펜스\'등 다수의 상용프로젝트 참여\r\n닉네임 폭풍류.인기 인디게임 \'RPG그녀\' 제작', '꿈과 희망을 심어주는 게임제작에 뜻을 두고 14년째 묵언 수행중인 양주현 선생님이\r\n여러분을 기다립니다. \'어서와,프로그래밍은 처음이지?\'', 'profile_yangjuhyun.jpg', 1),
	(11, '김재연', '게임 기획반', '게임 기획이란 끊임없이 상상하고 연구하고\r\n소통하는 일', '가마소프트. 네이브진 대표이사 및 기획 총괄\r\n청강문화산업대학 게임과 기획 외래교수 경력\r\n게임기획 공모전 다수 수상', '김재연 선생님은 인생의 절반을 게임 개발과 함께 한 최고의 경험과 실력을 \r\n가진 골수 기획자입니다.', 'profile_kimjaeyeon.jpg', 1);
